import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function OrderDetails() {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await api.get(`/orders/order/${orderId}`);
        const baseOrder = res.data;

        const itemsWithImages = await Promise.all(
          baseOrder.items.map(async (item) => {
            try {
              const productRes = await api.get(`/products/get/${item.productId}`);
              return {
                ...item,
                productImageUrl: productRes.data.imageUrl || null,
              };
            } catch (error) {
              console.error(`Failed to fetch product ${item.productId}`, error);
              return { ...item, productImageUrl: null };
            }
          })
        );

        setOrder({ ...baseOrder, items: itemsWithImages });
      } catch (err) {
        console.error(err);
        alert('Failed to load order');
      } finally {
        setLoading(false);
      }
    };

    fetchOrder();
  }, [orderId]);

  const cancelOrder = async () => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      try {
        await api.delete(`/orders/${orderId}`);
        alert('Order cancelled');
        navigate('/orders');
      } catch (err) {
        console.error(err);
        alert('Failed to cancel order');
      }
    }
  };

  if (loading) return <div>Loading order...</div>;
  if (!order) return <div>No order found.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Order #{order.id}</h2>
      <p><strong>Status:</strong> {order.status}</p>
      <p><strong>Total:</strong> ₹{order.total}</p>
      <p><strong>Created At:</strong> {new Date(order.createdAt).toLocaleString()}</p>

      <h3>Shipping Address</h3>
      <p>
        {order.shippingAddress.fullName},<br />
        {order.shippingAddress.addressLine1},<br />
        {order.shippingAddress.addressLine2 && (
          <>
            {order.shippingAddress.addressLine2},<br />
          </>
        )}
        {order.shippingAddress.city}, {order.shippingAddress.state} - {order.shippingAddress.postalCode}<br />
        {order.shippingAddress.country}<br />
        Phone: {order.shippingAddress.phone}
      </p>

      <h3>Items</h3>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
        {order.items.map(item => (
          <div
            key={item.id}
            style={{
              display: 'flex',
              alignItems: 'center',
              background: '#f9f9f9',
              padding: '10px',
              borderRadius: '6px',
              boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
            }}
          >
            <img
              src={item.productImageUrl || '/placeholder.png'}
              alt={item.productName}
              style={{
                width: '100px',
                height: '100px',
                objectFit: 'cover',
                borderRadius: '6px'
              }}
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = '/placeholder.png';
              }}
            />
            <div style={{ marginLeft: '20px', flex: 1 }}>
              <h4>{item.productName}</h4>
              <p>Unit Price: ₹{item.unitPrice}</p>
              <p>Quantity: {item.quantity}</p>
              <p>Subtotal: ₹{(item.unitPrice * item.quantity).toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>

      <button
        onClick={cancelOrder}
        style={{
          marginTop: '20px',
          backgroundColor: 'red',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        Cancel Order
      </button>
    </div>
  );
}

export default OrderDetails;
