import React, { useState, useEffect } from 'react';
import api from '../services/api';       // ← use our pre‐configured axios instance
import ProductCard from '../components/ProductCard';

function Home() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  // On mount: fetch all categories and all products
  useEffect(() => {
    fetchCategories();
    fetchProducts(); 
  }, []);

  // Fetch category list from /categories/getCategory
  const fetchCategories = async () => {
    try {
      const res = await api.get('/categories/getCategory');
      setCategories(res.data);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  // Fetch products, either by category or all
  // If `category` is falsy (''), it fetches every product
  const fetchProducts = async (category = '') => {
    try {
      let res;
      if (category) {
        // Example: GET /v1/products/category/byname?name=Electronics
        res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(category)}`);
      } else {
        // Example: GET /v1/products/getproducts
        res = await api.get('/v1/products/getproducts');
      }
      setProducts(res.data);
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  // Handler when a category button is clicked
  const handleCategoryClick = (categoryName) => {
    setSelectedCategory(categoryName);
    fetchProducts(categoryName);
  };

  return (
    <div className="container">
      {/* — Category Filter Buttons — */}
      <div style={{ margin: '20px 0' }}>
        {/* “All” button clears the filter */}
        <button
          className="button"
          onClick={() => {
            setSelectedCategory('');
            fetchProducts();
          }}
        >
          All
        </button>

        {/* Map each category to a button */}
        {categories.map((cat) => (
          <button
            key={cat.id}
            className="button"
            style={{ marginLeft: '10px' }}
            onClick={() => handleCategoryClick(cat.name)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      {/* — Product Grid — */}
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {products
          // guard against any null/undefined product entries
          .filter((p) => p && p.id)
          .map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
      </div>
    </div>
  );
}

export default Home;
