import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Cart() {
  const [cart, setCart] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // On mount, verify token and fetch the cart
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    fetchCart();
    // Only run once on mount
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchCart = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const res = await api.get('/v1/carts');
      setCart(res.data);
    } catch (err) {
      console.error('Error fetching cart:', err);
      if (err.response?.status === 401) {
        // If unauthorized, force login
        navigate('/login');
      }
    }
  };

  const handleRemove = async (itemId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      await api.delete(`/v1/carts/items/${itemId}`);
      fetchCart();
    } catch (err) {
      console.error('Error removing item:', err);
      if (err.response?.status === 401) {
        navigate('/login');
      }
    }
  };

  const handleUpdate = async (itemId, quantity) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      await api.put(`/v1/carts/items/${itemId}`, { quantity });
      fetchCart();
    } catch (err) {
      console.error('Error updating item:', err);
      if (err.response?.status === 401) {
        navigate('/login');
      }
    }
  };

  const handleClear = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      await api.delete('/v1/carts');
      setCart({ items: [], total: 0 });
    } catch (err) {
      console.error('Error clearing cart:', err);
      if (err.response?.status === 401) {
        navigate('/login');
      }
    }
  };

  if (!cart) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.items.map((item) => (
            <div
              key={item.itemId}
              style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '10px',
                background: 'white',
                padding: '10px',
                borderRadius: '4px',
              }}
            >
              <img
                src={item.product.imageUrl}
                alt={item.productName}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
              />
              <div style={{ marginLeft: '20px', flex: 1 }}>
                <h4>{item.productName}</h4>
                <p>Price: ₹{item.price}</p>
                <p>Subtotal: ₹{item.subtotal}</p>
                <div>
                  <label>Quantity:</label>
                  <input
                    type="number"
                    value={item.quantity}
                    min="1"
                    onChange={(e) =>
                      handleUpdate(item.itemId, e.target.value)
                    }
                    style={{ marginLeft: '8px', width: '60px' }}
                  />
                </div>
                <button
                  className="button"
                  onClick={() => handleRemove(item.itemId)}
                  style={{ marginTop: '10px' }}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
          <h3>Total: ₹{cart.total}</h3>
          <button
            className="button"
            onClick={() => {
              const token = localStorage.getItem('token');
              if (!token) {
                navigate('/login');
                return;
              }
              navigate('/checkout');
            }}
            style={{ marginTop: '10px' }}
          >
            Proceed to Checkout
          </button>
          <button
            className="button"
            onClick={handleClear}
            style={{ marginTop: '10px', marginLeft: '10px' }}
          >
            Clear Cart
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
