import React from 'react';
import { Link } from 'react-router-dom';

function ProductCard({ product }) {
  if (!product || !product.id) return null; // Guard clause

  return (
    <div className="card" style={{ width: '200px', margin: '10px' }}>
      <img src={product.image} alt={product.name} style={{ width: '100%', height: '200px' }} />
      <div className="card-body">
        <h4>{product.name}</h4>
        <p>â‚¹{product.price}</p>
        <Link to={`/product/${product.id}`} className="button">View</Link>
      </div>
    </div>
  );
}

export default ProductCard;
