import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchProduct();
  }, []);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddToCart = async () => {
    // Check for JWT token in localStorage
    const token = localStorage.getItem('token');
    if (!token) {
      // If no token, redirect to Login page
      alert('Please login to add items to cart');
      return navigate('/login');
    }

    try {
      await api.post(
        '/v1/carts/items',
        { productId: product.id, quantity },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      navigate('/cart');
    } catch (err) {
      console.error('Add to cart error:', err);
      // If the token was invalid/expired, redirect to login
      if (err.response && err.response.status === 401) {
        alert('Session expired, please login again');
        return navigate('/login');
      }
      alert('Failed to add product to cart.');
    }
  };

  const handleBuyNow = () => {
    // Reuse handleAddToCart for token check and cart update
    handleAddToCart();
    // After adding to cart, go to checkout
    navigate('/checkout');
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div className="container" style={{ display: 'flex', marginTop: '20px' }}>
      <img
        src={product.imageUrl}
        alt={product.name}
        style={{ width: '400px', height: '400px', objectFit: 'cover' }}
      />
      <div style={{ marginLeft: '20px' }}>
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p>
          <strong>Price:</strong> â‚¹{product.price}
        </p>
        <p>
          <strong>Brand:</strong> {product.brand}
        </p>
        <div>
          <label>Quantity:</label>
          <input
            type="number"
            value={quantity}
            min="1"
            onChange={(e) => setQuantity(e.target.value)}
          />
        </div>
        <button
          className="button"
          onClick={handleAddToCart}
          style={{ marginTop: '10px' }}
        >
          Add to Cart
        </button>
        <button
          className="button"
          onClick={handleBuyNow}
          style={{ marginTop: '10px', marginLeft: '10px' }}
        >
          Buy Now
        </button>
      </div>
    </div>
  );
}

export default ProductDetail;
