import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: ''
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const res = await api.get('/v1/carts');
      console.log('Cart data:', res.data); // Log to inspect structure
      setCart(res.data);
    } catch (err) {
      console.error('Fetch cart error:', err.response?.data || err.message);
      alert('Failed to load cart. Please try again.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        alert('Please log in to proceed with checkout.');
        navigate('/login');
        return;
      }

      // Prepare items for PaymentIntentRequest
      const items = cart.items.map((item) => ({
        productId: item.productId,
        quantity: item.quantity,
        price: Math.round(item.price * 100) // Convert to cents
      }));

      // Calculate total amount in cents
      const amount = Math.round(cart.total * 100); // Convert to cents

      // Log request payload for debugging
      const paymentIntentPayload = {
        userId: parseInt(userId),
        currency: 'INR',
        paymentMethodId: 'pm_card_visa',
        amount: amount,
        items: items
      };
      console.log('PaymentIntentRequest:', paymentIntentPayload);

      const paymentIntentRes = await api.post('/api/payment/create-payment-intent', paymentIntentPayload);

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const cardElement = elements.getElement(CardElement);
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        },
      });

      if (error) {
        console.error('Payment error:', error);
        alert(`Payment failed: ${error.message}`);
        setLoading(false);
        return;
      }

      // Prepare cartItems for checkout request
      const cartItems = cart.items.map((item) => ({
        productId: item.productId,
        quantity: item.quantity
      }));

      // Prepare shipping address
      const shippingAddress = {
        fullName: address.fullName,
        line1: address.line1,
        line2: address.line2 || null, // line2 is optional
        city: address.city,
        state: address.state,
        postalCode: address.postalCode,
        country: address.country,
        phone: address.phone
      };

      // Log checkout payload for debugging
      const checkoutPayload = {
        userId: parseInt(userId),
        paymentIntentId: paymentIntent.id,
        shippingAddress: shippingAddress,
        cartItems: cartItems
      };
      console.log('CheckoutRequest:', checkoutPayload);

      const checkoutRes = await api.post('/api/payment/checkout', checkoutPayload);

      alert(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate('/');
    } catch (err) {
      console.error('Checkout error:', err.response?.data || err.message);
      alert(`Checkout failed: ${err.response?.data?.error || 'Please try again.'}`);
      setLoading(false);
    }
  };

  if (!cart) return <div>Loading...</div>;

  return (
    <div className="container" style={{ maxWidth: '600px', marginTop: '20px' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        <h3>Shipping Address</h3>
        {['fullName', 'line1', 'line2', 'city', 'state', 'postalCode', 'country', 'phone'].map((field) => (
          <div key={field} style={{ marginBottom: '10px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>
              {field.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:
            </label>
            <input
              type="text"
              value={address[field]}
              onChange={(e) => setAddress({ ...address, [field]: e.target.value })}
              required={field !== 'line2'} // line2 is optional
              style={{ width: '100%', padding: '8px', borderRadius: '4px', border: '1px solid #ccc' }}
            />
          </div>
        ))}
        <h3>Payment Details</h3>
        <CardElement options={{ style: { base: { fontSize: '16px', color: '#424770', '::placeholder': { color: '#aab7c4' } } } }} />
        <button
          className="button"
          type="submit"
          disabled={!stripe || loading}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#0070f3',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Processing...' : `Pay â‚¹${cart.total}`}
        </button>
      </form>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
